function[len,v_count,individual] = RouteLength(p, demand, distance, m, K)individual = [0];capacity_used = 0;len = 0;v_count = 0;%----插入0表示卫星点----for i = 1:m    if (capacity_used + demand(p(1,i),1) <= K)        individual= [individual p(1,i)];        capacity_used = capacity_used + demand(p(1,i),1);    else        individual = [individual 0];        individual = [individual p(1,i)];        capacity_used = demand(p(1,i),1);        v_count = v_count + 1;    end    endindividual = [individual 0];%disp(individual);v_count = v_count + 1;%----计算距离----for i = 1:(size(individual,2)-1)    a = individual(1,i) + 1;    b = individual(1,i+1) + 1;    len = len + distance(a, b);   endend