function[len] = RouteLength(p, demand, distance, m, K2)disp(p);individual = [0];capacity_used = 0;len = 0;%----插入0表示卫星点----for i = 1:m    if (capacity_used + demand(1,p(1,i)) <= K2)        individual= [individual p(1,i)];        capacity_used = capacity_used + demand(1,p(1,i));    else        individual = [individual 0];        individual = [individual p(1,i)];        capacity_used = demand(1,p(1,i));    end        endindividual = [individual 0];%----计算距离----for i = 1:size(individual,2) - 1    len = len + distance(individual(1,i), individual(1,i+1));endend