%----Assigns each customer to the satellite with the smallest distance----function[assign_table] = assignCus2Sat(SatCusDist, sat_num, custom_num, demand, K2, m2)assign_table = zeros(sat_num, custom_num);condition = zeros(sat_num,2); %第1列表示当前负载，第2列表示当前车辆数量[custom_demand_sort, sort_custom] = sort(demand, 'descend');%[min_dist, nearest_sat] = min(SatCusDist);[sat_nearest_sort, sort_sat] = sort(SatCusDist);%disp(sat_nearest_sort);%disp(sort_sat);condition(:,2) = 1; %disp(condition_record);for i = 1:custom_num    custom_index = sort_custom(i,1);    sat_index = sort_sat(1, custom_index);    %检查是否满足条件    if condition(sat_index, 1) < K2        conditon(sat_index, 1) = condition(sat_index, 1) + demand(custom_index);            elseif sum(condition(:,2)) < m2        condition(sat_index, 2) = condition(sat_index, 2) + 1;        condition(sat_index, 1) = 0;    else        %reassignment        assign_table(sat_index, custom_index) = 0;        sat_index = sort_sat(2, custom_index); % 这里只是将客户分配到第二个最近点    end    assign_table(sat_index, custom_index) = 1;   endend