function[assign_table] = assignCus2Sat(SatCusDist, sat_num, custom_num, demand, K2, m2)assign_table = zeros(sat_num, custom_num);condition = zeros(sat_num,2); %第1列表示当前负载，第2列表示当前车辆数量[custom_demand_sort, sort_custom] = sort(demand, 'descend');[sat_nearest_sort, sort_sat] = sort(SatCusDist);condition(:,2) = 1; s = 1;for i = 1:custom_num    custom_index = sort_custom(i,1);    sat_index = sort_sat(s, custom_index);    %检查是否满足条件    result = checkCondition(condition,K2,m2,sat_index,custom_index,demand);        while result == 0        assign_table(sat_index, custom_index) = 0;        s = s + 1;        sat_index = sort_sat(s, custom_index);        result = checkCondition(condition,K2,m2,sat_index,custom_index,demand);    end    assign_table(sat_index, custom_index) = 1;   endend