function[output] = calOutput(best_route, distance, demand)zero_count = 0;index = zeros(1,0);cost = 0;weight = 0;route = zeros(1,0);for i = 1:size(best_route,2)    if best_route(1,i) == 0        zero_count = zero_count + 1;        index = [index i];   %记录0的位置    endendoutput = cell(zero_count-1,4);  %第一列表示子路径开销，第二列表示载重量，第三列表示节点数，第四列表示车辆路径for j = 1:(zero_count-2)    for k = index(1,j) : (index(1,j+1)-1)        %路径开销        a = best_route(1,k) + 1;        b = best_route(1,k+1) + 1;        cost = cost + distance(a,b);        %载重量 车辆路径        if best_route(1,k) ~= 0            weight = weight + demand(best_route(1,k),1);            route = [route best_route(1,k)];        end           end        output{j,1} = cost;    output{j,2} = weight;    output{j,3} = index(1,j+1) - index(1,j) - 1;    %disp(output{j,3});    output{j,4} = route;    %disp(output{j,4});    cost = 0;    weight = 0;    route = [];endx = index(1,zero_count-1);y = size(best_route,2);for k = x : y-1    a = best_route(1,k) + 1;    b = best_route(1,k+1) + 1;    cost = cost + distance(a,b);    if best_route(1,k) ~= 0       weight = weight + demand(best_route(1,k),1);       route = [route best_route(1,k)];    endendoutput{zero_count-1,1} = cost;output{zero_count-1,2} = weight;output{zero_count-1,3} = y-x-1;output{zero_count-1,4} = route;end