%----Calculate distance between satellites and customers----function[SatCusDist] = calSatCusDist(coord_sat, coord_cus, sat_num, custom_num)SatCusDist = zeros(sat_num, custom_num);for i = 1:sat_num    for j = 1:custom_num        SatCusDist(i,j) = sqrt((coord_sat(i,1) - coord_cus(j,1)).^2 + (coord_sat(i,2) - coord_cus(j,2)).^2);    endend%fprintf("\nSatCusDist = \n");%disp(SatCusDist);end