function[result] = checkCondition(condition,K2,m2,sat_index,custom_index,demand)result = 0;if (condition(sat_index, 1) + demand(custom_index,1)) <= K2    condition(sat_index, 1) = condition(sat_index, 1) + demand(custom_index,1);     result = 1;    elseif sum(condition(:,2)) < m2    condition(sat_index, 2) = condition(sat_index, 2) + 1;    condition(sat_index, 1) = 0;    result = 1;endend