function[best_solution, best_individual] = cvrp(CustomSet, demand, distance, K2)n = 50; %种群大小m = size(CustomSet,2);iterition = 50; %迭代次数(遗传代数）Pc = 0.8; %交叉概率Pm = 0.2; %变异概率len = zeros(n,1);best_solution = 99999999;best_individual = zeros(1,m);%----随机父代----family = zeros(n, m);for i = 1:n    family(i,:) = CustomSet(randperm(m));endcounter = 0;while counter < iterition     %----计算路径长度----    for i = 1:n        [len(i,1)] = RouteLength(family(i,:), demand, distance, m, K2);           end            %----选择----    individual_fitness = zeros(n,1);    family_fitness = 0;    cur_best_distance = 99999999;     new_family = zeros(n, m);    for i = 1:n        cur_distance = len(i,1);        if cur_distance < cur_best_distance            cur_best_distance = cur_distance;            cur_best_individual = family(i,:);        end        if cur_distance < best_solution            best_solution = cur_distance;            best_individual = family(i,:);        end        individual_fitness(i,1) = 1 / len(i,1);        family_fitness = family_fitness + individual_fitness(i,1);    end        select_probability = zeros(n,1);    probability = 0;    for i = 1:n        probability = individual_fitness(i,1) / family_fitness;        if i ~= 1            select_probability(i,1) = select_probability(i-1,1) + probability;        else            select_probability(i,1) = probability;        end    end        for i = 1:n        x = rand;        if i == 1 && x < 0.5            new_family(i,:) = best_individual;        else            for j = 1:n                if x < select_probability(j,1)                    new_family(i,:) = family(j,:);                    break;                end            end        end    end        family = zeros(n,m);    family = new_family;    %----交叉----    for i = 1:2:n        if Pc > rand            A = family(i,:);            B = family(i+1,:);            [A,B] = intercross(A,B);            family(i,:) = A;            family(i+1,:) = B;        end    end        %----变异----    for i = 1:n        if Pm > rand            family(i,:) = mutate(family(i,:));        end    end        counter = counter + 1;endend