function[A,B] = intercross(A,B)%确定交叉宽度L = length(A);%p = unidrnd(L-1);%W = unidrnd(L-p-1);%if L <= 2%    W = 2;%elseif L <= 5%    W = 5;if L<=10  %确定交叉宽度    W=5;elseif ((L/10)-floor(L/10))>=rand&&L>10    W=ceil(L/10)+8;else    W=floor(L/10)+8;endp=unidrnd(L-W+1);%随机选择交叉范围，从p到p+W%执行交叉操作for i=1:W    x=find(A==B(1,p+i-1));    y=find(B==A(1,p+i-1));    [A(1,p+i-1),B(1,p+i-1)]=exchange(A(1,p+i-1),B(1,p+i-1));    [A(1,x),B(1,y)]=exchange(A(1,x),B(1,y));   endend