function[] = outputResult(layer1,layer2,totalcost)fprintf('total Cost = %0.2f\n', totalcost);fprintf('----------------layer1-------------------\n');for i = 1:size(layer1,1)    fprintf('Cost(%0.1f),Weight(%0.1f),Cust(%d) ',layer1{i,1},layer1{i,2},layer1{i,3});    fprintf('%d ',layer1{i,4});    fprintf('\n');    endfprintf('\n----------------layer2-------------------\n');Cost = 0;for i = 1:size(layer2,1)    output = layer2{i,1};    for j = 1:size(output,1)        Cost = Cost + output{j,1};        Vehicles = size(output,1);    end        fprintf('Cost=%0.2f Vehicles=%d\n', Cost, Vehicles);    for j = 1:size(output,1)                fprintf('Cost(%0.1f),Weight(%0.1f),Cust(%d) ',output{j,1},output{j,2},output{j,3});        fprintf('%d ',output{j,4});        fprintf('\n');    end    Cost = 0;    fprintf('\n');end       end