function [] = saveSpecialValue(record_totalcost,name_data)%best, worst, mean, median and standard variancebest = max(record_totalcost);worst = min(record_totalcost);mean = mean(record_totalcost);median = median(record_totalcost);standard_variance = var(record_totalcost);fid = fopen('record.txt','a');fprintf(fid,'%d\t%d\t%d\t%d\t%d\t',best,worst,mean,median,standard_variance);fprintf(fid,'\n');fclose(fid);end